srcDoc.css:
@charset "UTF-8";
.sidebar {
  background-color: #4A3AFF;
  width: calc(100% - 40px); /* Compense le padding */
  padding: 20px; /* Padding uniforme autour de la sidebar */
  color: #ffffff;
  font-family: Arial, sans-serif;
  height: calc(100vh - 40px);
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  overflow: hidden;
}

.menu {
  display: flex;
  flex-direction: column;
  justify-content: flex-start;
}

.menu-item {
  display: flex;
  align-items: center;
  padding: 10px 20px; /* Padding ajust√© pour √©quilibrer */
  font-size: 16px;
  cursor: pointer;
  border-radius: 8px;
  transition: background 0.3s ease;
  margin-bottom: 15px;
}

.menu-item:hover {
  background-color: #5F4CFF;
}

.menu-item.active {
  background-color: #342E99;
}

.icon {
  margin-right: 10px; /* Espacement entre l'ic√¥ne et le texte */
}

.profile-section {
  display: flex;
  align-items: center;
  padding: 10px 20px; /* Alignement du padding avec menu-item */
  margin-top: 20px;
  cursor: pointer;
  border-radius: 8px;
  transition: background 0.3s ease;
  position: relative;
  text-decoration: none;
  font-weight: normal;
  border-top: none;
  box-shadow: none;
}

.profile-section:hover {
  background-color: #5F4CFF;
}

.profile-pic {
  width: 40px;
  height: 40px;
  border-radius: 50%;
  margin-right: 15px;
}

.profile-details {
  display: flex;
  flex-direction: column;
  justify-content: center;
  margin-right: auto;
}

.profile-name {
  font-size: 16px;
  font-weight: bold;
  color: #ffffff;
}

.settings-icon {
  position: absolute;
  right: 20px; /* Alignement avec le padding de gauche */
  cursor: pointer;
  text-decoration: none;
  font-weight: normal;
}

